mapper.insert:
INSERT INTO competition_results (
    competition_id, athlete_id, athlete_name, final_rank, 
    final_score, total_shots, created_at
) VALUES (
    #{competitionId}, #{athleteId}, #{athleteName}, #{finalRank}, 
    #{finalScore}, #{totalShots}, #{createdAt}
)

INSERT INTO competition_results (
    competition_id, athlete_id, athlete_name, final_rank, 
    final_score, total_shots, created_at
) VALUES 

    (
        #{item.competitionId}, #{item.athleteId}, #{item.athleteName}, #{item.finalRank}, 
        #{item.finalScore}, #{item.totalShots}, #{item.createdAt}
    )

mapper.select:
SELECT * FROM competition_results 
WHERE competition_id = #{competitionId}
ORDER BY final_rank

SELECT * FROM competition_results 
WHERE competition_id = #{competitionId} AND athlete_id = #{athleteId}
LIMIT 1

SELECT * FROM competition_results 
WHERE athlete_id = #{athleteId}
ORDER BY created_at DESC
